import React, { Component } from 'react';
import { SearchBox } from './components/search-box/search-box.component';
import { CardList } from './components/card-list/card-list.component';

import './App.css';

class App extends Component {

  constructor(){


    super();
      this.state = {
        books: [],
        searchField: '',
      };
    }
    componentDidMount(){
      const library = {
        "book":[
          {"title":"Harry Potter", imgurl:'',"id":"0"},
          {
            "title":"Great Gasby", 
            imgurl:'',
            "id":"1"
          },
          {
            "title":"Gone with the Wind", 
            imgurl:'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1474154022l/3._SY475_.jpg',
            "id":"2"
          }
        ]
      }
      this.setState({
        books: library.book
      });
    }

    handleChange = e => {
      this.setState({searchField: e.target.value})
    }

  render(){
    const { books, searchField } = this.state;
    const filteredBooks = books.filter(book =>
        book.title.toLowerCase().includes(searchField.toLowerCase())
      )

    return (
      <div className="App">
      <h1>Books List</h1>
      <SearchBox placeholder='search books' 
          handleChange={this.handleChange}/>
          <p><br></br></p>
        <CardList books = {filteredBooks}>
        </CardList>
      </div>
    );
  }
}
export default App;
